'''keys: greedy, sort at first O(nlogn), then solution O(n)Solutions:Similar:T:S:'''from typing import Listclass Solution:    # T: O(longn); S: O(1)    # sort by priceA - priceB, send first N to A    def twoCitySchedCost(self, costs: List[List[int]]) -> int:        costs.sort(key = lambda x: x[0] - x[1])                res = 0        n = len(costs)//2 # always even number of people        for i in range(n):             res += costs[i][0] + costs[i+n][1]        return res