'''keys:Solutions:Similar:T:S:'''from typing import Listfrom functools import reduceimport operatorclass Solution:    #  T: log(N), N is referring to the input n. For any integer, the number     # of digits is log(N) (base 10) where N is the number because each digit     # represents {digit}*10^(k-th location).    def subtractProductAndSum(self, n: int) -> int:        sum_ = 0; product = 1        while n > 0:            sum_ += n % 10            product *= n % 10            n //= 10        return product - sum_            # O(logN)    def subtractProductAndSum1(self, n):        A = list(map(int, str(n)))        return reduce(operator.mul, A) - sum(A)