'''keys: Solutions:Similar: T:S:'''from typing import List# Definition for a binary tree node.class TreeNode:    def __init__(self, val=0, left=None, right=None):        self.val = val        self.left = left        self.right = right        class Solution:        def removeLeafNodes1(self, root, target):        if root:            root.left = self.removeLeafNodes(root.left, target)            root.right = self.removeLeafNodes(root.right, target)            if root.val == target and root.left == root.right:                return None            return root            def removeLeafNodes(self, root: TreeNode, target: int) -> TreeNode:        if root.left:            root.left = self.removeLeafNodes(root.left, target)        if root.right:            root.right = self.removeLeafNodes(root.right, target)        return None if root.left == root.right and root.val == target else root