'''keys: Solutions:Similar:T:S:'''from typing import Listclass Solution:    def longestSubarray(self, nums: List[int], limit: int) -> int:        if not nums:            return         if len(nums) == 1:            return 0 <= limit        dp = [0] * len(nums)        for i in range(1, len(nums)):            tmp = nums[i] - nums[i-1]            if tmp <= limit:                dp[i] = dp[i-1] + 1            else:                dp[i] = dp[i-1]        return dp[-1]        