'''keys: Solutions:Similar:T:S:'''from typing import List# https://leetcode.com/problems/check-if-array-pairs-are-divisible-by-k/discuss/709691/Java-7ms-Simple-Solutionclass Solution:    # T: O(N); S: O(k)    '''    f a % k == x and b % k == k - x :    then (a + b) is divisible by k    '''    def canArrange(self, arr: List[int], k: int) -> bool:        ct = [0] * k        for num in arr:            ct[num % k] += 1                if ct[0] % 2 != 0: return False                for i in range(1, k//2 + 1):            if ct[i] != ct[k-i]:                return False        return True                    # one-liner    # not working for [2,2,2,2,2,2], k =6    def canArrange1(self, arr: List[int], k: int) -> bool:        return sum(arr) % k == 0