'''keys: Solutions:Similar: T:S:'''from typing import Listclass Solution:    # O(1) for S and T    '''    x & (x-1) is a way to set the rightmost 1-bit to zero    '''    def isPowerOfTwo2(self, n):        if n == 0:            return False        return n & (-n) == n            # official; T: (logN)    def isPowerOfTwo1(self, n):        if n == 0:            return False        while n % 2 == 0:            n //= 2        return n == 1            # self-made    def isPowerOfTwo(self, n: int) -> bool:        if not n or n < 0: return False        if n == 1: return True                while n > 1:            num, rem = divmod(n, 2)            # print (num, rem)            if rem != 0:                return False            n = num                    return True