'''keys: Solutions:Similar:T:S:'''from typing import Listclass Solution:        # O(N) for S and T    def hIndex1(self, citations: List[int]) -> int:        n = len(citations)        papers = [0] * (n+1)        # counting papers for each citation number        for c in citations:            if c >= n:                papers[n] += 1            else:                papers[c] += 1        ct = 0        for i in range(n, -1, -1):            ct += papers[i] # ct represents number of papers w at least i citations            if ct >= i: # i represents citation                return i        return 0            # T: O(NlogN); S: O(1)    def hIndex(self, citations: List[int]) -> int:        citations.sort(reverse=True)                h = 0        # j represents the number of citations for each paper        # if citations[i] > i, then papers 0 to i all have at least i+1 citations        for i, j in enumerate(citations):            if i < j: # j == citations[i]                h += 1        return h        