'''keys: Solutions:Similar:T:S:'''from typing import Listclass Solution:        # binary search    # T: O(logN); S: O(1)    def hIndex1(self, citations: List[int]) -> int:                citations = citations[::-1]        n = len(citations)        left, right = 0, n - 1        while left <= right:            pivot = left + (right - left) // 2            if citations[pivot] == pivot:                return pivot            elif citations[pivot] > pivot:                left = pivot + 1            else:                right = pivot - 1                return left            def hIndex(self, citations: List[int]) -> int:        citations = citations[::-1]        h = 0        for idx, citation in enumerate(citations):            if idx < citation:                h += 1        return h