'''keys: Solutions:Similar: T:S:'''from typing import Listclass Solution:    # T: O(log_3(n)); S: O(1)    def isPowerOfThree(self, n: int) -> bool:        if n == 0:            return False                while n % 3 == 0:            n //= 3        return n == 1        # O(1)    # https://leetcode.com/problems/power-of-three/discuss/77977/Math-1-liner-no-log-with-explanation    def isPowerOfThree1(self, n: int) -> bool:        return n > 0 == 3**19 % n