'''keys: Solutions:Similar: T:S:'''from typing import Listfrom collections import dequeclass MovingAverage:    ##### official T: O(1) and S: O(N)    def __init__(self, size: int):        self.size = size        self.queue = deque()        # number of elements seen so far        self.window_sum = 0        self.count = 0    def next(self, val: int) -> float:        self.count += 1        # calculate the new sum by shifting the window        self.queue.append(val)        tail = self.queue.popleft() if self.count > self.size else 0        self.window_sum = self.window_sum - tail + val        return self.window_sum / min(self.size, self.count)    ##### self-made, T: O(N), S: O(N), N as the size of the moving window    def __init__(self, size: int):        """        Initialize your data structure here.        """        self.size = size        self.arr = deque([])            def next(self, val: int) -> float:        if len(self.arr) >= self.size:            self.arr.popleft()            self.arr.append(val)        if len(self.arr) < self.size:            self.arr.append(val)                    return sum(self.arr)/len(self.arr)# Your MovingAverage object will be instantiated and called as such:# obj = MovingAverage(size)# param_1 = obj.next(val)