'''keys: Solutions:Similar: T:S:'''from typing import Listfrom collections import dequeclass MovingAverage:    # self-made, T: O(1), S: O(N), N as the size of the moving window    def __init__(self, size: int):        """        Initialize your data structure here.        """        self.size = size        self.arr = deque([])            def next(self, val: int) -> float:        if len(self.arr) >= self.size:            self.arr.popleft()            self.arr.append(val)        if len(self.arr) < self.size:            self.arr.append(val)                    return sum(self.arr)/len(self.arr)# Your MovingAverage object will be instantiated and called as such:# obj = MovingAverage(size)# param_1 = obj.next(val)