'''keys: Solutions:Similar:T:S:'''from typing import Listfrom collections import Counter, dequeimport heapqclass Solution:    # T: O(nlog26), i.e., O(n)    # https://leetcode.com/problems/rearrange-string-k-distance-apart/discuss/83192/Java-7-version-of-PriorityQueue-O(nlogn)-with-comments-and-explanations    # consider the chars with max frequency first    # sort by frequency first, use -frequency in heapq, then sort by    # lexciographical order, with count as key    def rearrangeString(self, s: str, k: int) -> str:        res = ""        if not s: return res                maxHeap = []        hashmap = Counter(s)        # python's heapq is min heap by default. So create a customized heap        # order is to have each element on the heap to be a tuple. The first        # tuple element is the one will be accpeted to be normal python         # comparisons. Assign the count value to be negative, so that the         # heap to be a max heap.        for c, ct in hashmap.items():            heapq.heappush(maxHeap, (-ct, c))                waitQueue = deque()                while maxHeap:            ct, c = heapq.heappop(maxHeap)            res += c            # current[0] -= 1 tuple is immutable, cannot assign value             cur = (-1 * ct - 1, c) # postive count - 1            waitQueue.append(cur)                        if len(waitQueue) < k:                continue            # i.e., len(waitQueue) == k, already place k chars            front = waitQueue.popleft()            if front[0] > 0: # still char left                heapq.heappush(maxHeap, (-front[0], front[1]))                        return res if len(res) == len(s) else ""        