'''keys: Solutions:Similar: T:S:'''from typing import Listimport random# https://leetcode.com/problems/random-pick-index/discuss/88153/Python-reservoir-sampling-solution.class Solution:    def __init__(self, nums: List[int]):        self.nums = nums    def pick(self, target: int) -> int:        res = None        ct = 0        for idx, num in enumerate(self.nums):            if num == target:                ct += 1                chance = random.randint(1, ct)                if chance == ct:                    res = idx        return res        # Your Solution object will be instantiated and called as such:obj = Solution([4,9,3,3,3,3])param_1 = obj.pick(3)        