'''keys: Solutions:Similar: T:S:'''from typing import Listclass Solution:        # self-made, T: O(N), S: O(1)    def fizzBuzz(self, n: int) -> List[str]:        res = []        if not n: return         # if n == 1: return ["1"]        for i in range(1, n+1):            if i < 3:                res.append(str(i))            elif i % 3 == 0 and i % 5 == 0: res.append("FizzBuzz")            elif i % 3 == 0:                res.append("Fizz")            elif i % 5 == 0:                res.append("Buzz")            else:                res.append(str(i))                        return res            # no mod operation    def fizzBuzz1(self, n):        result = []        base3 = 3        base5 = 5        base15 = 15                for i in range(1, n+1):            if i == base15:                result.append('FizzBuzz')                                base3 = base15 + 3                base5 = base15 + 5                base15 += 15            elif i == base3:                result.append('Fizz')                base3 += 3            elif i == base5:                result.append("Buzz")                base5 += 5            else:                result.append(str(i))        return result