'''keys: sliding window approach in a linear timeSolutions:Similar: T:S:'''from typing import Listfrom collections import Counterclass Solution:    # array??? ignore this, better use the Counter version    def findAnagrams1(self, s: str, p: str) -> List[int]:        ns, np = len(s), len(p)        if ns < np:            return []        p_count, s_count = [0] * 26, [0] * 26        # build reference array using string p        for ch in p:            p_count[ord(ch) - ord('a')] += 1                output = []        # sliding window on the string s        for i in range(ns):            # add one more letter             # on the right side of the window            s_count[ord(s[i]) - ord('a')] += 1            # remove one letter             # from the left side of the window            if i >= np:                s_count[ord(s[i - np]) - ord('a')] -= 1            # compare array in the sliding window            # with the reference array            if p_count == s_count:                output.append(i - np + 1)                return output                # Recompute sliding window counter sCount at each step by adding     # one letter on the right and removing one letter on the left.    # T: O(Ns+Np)    # S: O(1)    def findAnagrams(self, s: str, p: str) -> List[int]:        ns, np = len(s), len(p)        if ns < np:            return []        p_count = Counter(p)        s_count = Counter() # for string in the window                output = []        # sliding window on the string s        for i in range(ns):            # add one more letter             # on the right side of the window            s_count[s[i]] += 1            # remove one letter             # from the left side of the window            if i >= np:                if s_count[s[i - np]] == 1:                    del s_count[s[i - np]]                else:                    s_count[s[i - np]] -= 1            # compare array in the sliding window            # with the reference array            if p_count == s_count:                output.append(i - np + 1)                return output                                                