'''keys: Solutions:Similar:T:S:'''from typing import Listclass Solution:    # T: O(N); S: O(1)    def compress(self, chars: List[str]) -> int:        idxRes = 0; ptr = 0 # ptr showing the current processing char        while ptr < len(chars):            cur_char = chars[ptr]            ct = 0 # ct of cur_char            while ptr < len(chars) and chars[ptr] == cur_char:                ptr += 1                ct += 1            chars[idxRes] = cur_char            idxRes += 1            # notice that ct == 1, we dont append the ct... shitty one            if ct != 1: # if ct == 1, append the char, the length does not change                for c in str(ct): # ["12"] needs to be shown as ["1", "2"]                    chars[idxRes] = c                    idxRes += 1        # chars[:] = chars[:idxRes]         return idxRes