'''keys: Solutions:Similar:T:S:'''from typing import Listclass Solution:    # T: O(N); S: O(1)    def compress(self, chars: List[str]) -> int:        idxRes = 0; ptr = 0 # ptr showing the current processing char        while ptr < len(chars):            cur_char = chars[ptr]            ct = 0 # ct of cur_char            while ptr < len(chars) and chars[ptr] == cur_char:                ptr += 1                ct += 1            # reaching the end or meeting a different char            chars[idxRes] = cur_char # saving chr            idxRes += 1            # notice that ct == 1, we dont append the ct... shitty one            if ct != 1: # if ct == 1, append the char, the length does not change                for c in str(ct): # ["12"] needs to be shown as ["1", "2"]                    chars[idxRes] = c                    idxRes += 1        # chars[:] = chars[:idxRes]         return idxRes    # official    # T: O(N), S: O(1)    def compress(self, chars: List[str]) -> int:        anchor = write = 0        for read, c in enumerate(chars):            if read + 1 == len(chars) or chars[read + 1] != c:                chars[write] = chars[anchor]                write += 1                if read > anchor:                    for digit in str(read - anchor + 1):                        chars[write] = digit                        write += 1                anchor = read + 1        return write