'''keys: Solutions:Similar:T:S:'''from typing import Listclass Solution:    # T: O(N); S: O(1)    def compress(self, chars: List[str]) -> int:        idxRes = 0; idx = 0        while idx < len(chars):            cur_char = chars[idx]            ct = 0            while idx < len(chars) and chars[idx] == cur_char:                idx += 1                ct += 1            chars[idxRes] = cur_char            idxRes += 1            if ct != 1: # if ct == 1, append the char, the length does not change                for c in str(ct): # ["12"] needs to be shown as ["1", "2"]                    chars[idxRes] = c                    idxRes += 1        # chars[:] = chars[:idxRes]         return idxRes