'''keys: OrderedDict is a dict + double linked list.A freq2node is a dict of OrderedDict, so you can look up like this freq2node[freq][key] to remove/update the node in O(1), or freq2node[freq].popitem(last=True) to remove the oldest node in O(1).Solutions:Similar: T:S:'''from typing import Listfrom collections import defaultdictfrom collections import OrderedDictclass Node:    def __init__(self, key, val, freq):        self.key = key        self.val = val        self.freq = freq        class LFUCache:    def __init__(self, capacity: int):        self.cap = capacity        self.key2node = {} # k: key, v: Node        # k: frequency, v: list of nodes (ordered)        self.freq2node = defaultdict(OrderedDict)        self.minFreq = None            def get(self, key: int) -> int:        if key not in self.key2node:            return -1        node = self.key2node[key]        # delete the node from the current frequency list        del self.freq2node[node.freq][key]        # insert the node the higher frequency list        # actually it's instered to the very beginning        node.freq += 1        self.freq2node[node.freq][key] = node        # if current frequency no longer has node        if not self.freq2node[node.freq]:            del self.freq2node[node.freq]        # increase minFreq if minFreq list no longer has node        if not self.freq2node[self.minFreq]:            self.minFreq += 1                return node.val            def put(self, key: int, value: int) -> None:        if not self.cap: # capacity is 0            return                 if key in self.key2node:            self.key2node[key].val = value            self.get(key) # update frequency, put makes freq+1 too            return                if len(self.key2node) == self.cap:            # OrderedDict.popitem(last=True) is LIFO            # OrderedDict.popitem(last=False) is FIFO, like queue            # it return key and value!!!            k, n = self.freq2node[self.minFreq].popitem(last=False)             del self.key2node[k]                 # add the new Node to the frequency list        self.key2node[key] = Node(key, value, 1)        self.freq2node[1][key] = Node(key, value, 1)        # for new node, the frequency is 1        self.minFreq = 1        return# Your LFUCache object will be instantiated and called as such:# obj = LFUCache(capacity)# param_1 = obj.get(key)# obj.put(key,value)                                                        