'''keys: Solutions:Similar: T:S:'''from typing import Listclass Solution:    # sliding window; T: O(N); S: O(1)    def findLengthOfLCIS(self, nums: List[int]) -> int:        res = 0        ptr = 0        for i in range(len(nums)):            if i and nums[i] <= nums[i-1]:                ptr = i            res = max(res, i - ptr + 1)        return res                    # easy one    def findLengthOfLCIS1(self, nums: List[int]) -> int:        if not nums: return 0        res = 1        ct = 1        for i in range(1, len(nums)):            if nums[i] > nums[i-1]:                ct += 1            else:                ct = 1            res = max(res, ct)        return res