'''keys: Solutions:Similar: T:S:'''from typing import Listclass Solution:    # T: O(N), S: O(1)    def pivotIndex(self, nums: List[int]) -> int:        if not nums: return -1                preSum = 0        allSum = sum(nums)        for idx, num in enumerate(nums):            if preSum == (allSum - num)/2:                return idx            preSum += num        return -1            