'''keys: https://leetcode.com/problems/partition-labels/discuss/298474/Python-two-pointer-solution-with-explanationSolutions:Similar: T:S:'''from typing import Listclass Solution:    # O(N) for T and O(1) for S    def partitionLabels(self, S: str) -> List[int]:        rightmost = {c:i for i, c in enumerate(S)}       	left, right = 0, 0 # for each partition              	result = []       	for i, letter in enumerate(S):              		right = max(right,rightmost[letter])       	       		if i == right: # comes to the end of current partition       			result += [right-left + 1]       			left = i+1              	return result