'''keys: Solutions:Similar:T:S:'''from typing import List# https://leetcode.com/problems/swap-adjacent-in-lr-string/discuss/217070/Python-using-corresponding-position-class Solution:    # T: O(N)    # S: O(1)    def canTransform(self, start: str, end: str) -> bool:        if len(start) != len(end):            return False                # track the idx of L and R, s for start and e for end        A = [(s, idx) for idx, s in enumerate(start) if s == "L" or s == "R"]        B = [(e, idx) for idx, e in enumerate(end) if e == "L" or e == "R"]        # number of L and R must be equal        if len(A) != len(B): return False                for (s, i), (e, j) in zip(A, B):            if s != e: # since RXLX -> XRLX, the order of R and L won't change                return False            if s == "L":                if i < j: # XL -> LX, L will move to the left                    return False            if s == "R":                if i > j:                    return False        return True