'''keys: Solutions:Similar: T:S:'''from typing import Listclass Solution:    def peakIndexInMountainArray(self, A: List[int]) -> int:        l, r = 0, len(A) - 1        while l < r:            mid = l + (r-l)//2            if A[mid] > A[mid+1]:                r = mid            else:                l = mid + 1        return l        # some cheat trick    def peakIndexInMountainArray1(self, A: List[int]) -> int:        return A.index(max(A))