'''keys: Solutions:Similar: T:S:'''from typing import Listclass StockSpanner:    # T and S: O(Q), Q as number of calls of self.next    def __init__(self):        self.stack = []    def next(self, price: int) -> int:        w = 1        while self.stack and price >= self.stack[-1][0]:            w += self.stack.pop()[1]        self.stack.append((price, w))        return w