'''keys: Solutions:Similar: T:S:'''from typing import Listimport stringclass Solution:    # union find    def equationsPossible1(self, equations: List[str]) -> bool:        pass                # a concise verion    # T: O(N)    def equationsPossible(self, equations: List[str]) -> bool:        def find(x):            if uf[x] != x:                uf[x] = find(uf[x])            return uf[x]                uf = {a: a for a in string.ascii_lowercase}        for a, e, _, b in equations:            if e == "=":                '''                Firt pass all "==" equations.                Union equal letters together                Now we know which letters must equal to the others.                '''                uf[find(a)] = find(b)        # Second pass all "!=" inequations,        # Check if there are any contradict happens.        return not any(e == "!" and find(a) == find(b) for a, e, _, b in equations)