'''keys: Solutions:Similar: T:S:'''from typing import Listclass Solution:	# https://leetcode.com/problems/find-k-closest-elements/discuss/106426/JavaC%2B%2BPython-Binary-Search-O(log(N-K)-%2B-K)    def findClosestElements(self, arr: List[int], k: int, x: int) -> List[int]:    	# assume A[mid] ~ A[mid + k] is sliding window        left, right = 0, len(arr) - k - 1 # since we need a window as length k        while left <= right:            mid = (left + right) // 2            # -------A[mid]------------------x---A[mid + k]----------            # need to move the window right            if x - arr[mid] > arr[mid + k] - x:                left = mid + 1            else:                right = mid - 1        return arr[left:left + k]