'''keys: Solutions:Similar: T:S:'''from typing import Listclass Solution:    # two pointer; O(N) for S and T    def sortedSquares1(self, A: List[int]) -> List[int]:        res = []        l, r = 0, len(A) - 1        while l <= r:            left, right = abs(A[l]), abs(A[r])            if left > right:                res.append(left*left)                l += 1            else:                res.append(right*right)                r -= 1        return res[::-1] # in-place reverse                                    # sort    def sortedSquares(self, A: List[int]) -> List[int]:        if not A:            return []        return sorted([x*x for x in A])