'''keys: Solutions:Similar:T:S:'''from typing import Listclass Solution:    """    @param k: The k    @param a: The A, string    @param b: The B, string    @return: The answer    """    def addition(self, k, a, b):        # Write your code here        if len(a) > len(b):            a, b = b, a                    a = [int(c) for c in a]        ans = [0] + [int(c) for c in b] # [0] for the potential carry                i, j = len(a) - 1, len(ans) - 1        while j >= 0: # j is the longer one            ans[j] += a[i] if i >= 0 else 0            ans[j-1] += ans[j] // k # update carry first            ans[j] %= k # remainder            i -= 1            j -= 1        # remove leading 0        i = 0        while ans[i] == 0:            i += 1        ans = ans[i:]        return ''.join([str(x) for x in ans])