'''keys: Solutions:Similar:T:S:'''from typing import List"""Definition of TreeNode:class TreeNode:    def __init__(self, val):        self.val = val        self.left, self.right = None, None"""class Solution:    """    @param root1: the first tree    @param root2: the second tree    @return: returns whether the leaf sequence is the same    """    def leafSimilar(self, root1, root2):        # write your code here.                def leaf(node, res):            if not node:                return            if not node.left and not node.right:                res.append(node.val)            if node.left: # not necessary                leaf(node.left, res)            if node.right:                leaf(node.right, res)                                leaf1, leaf2 = [], []        leaf(root1, leaf1)        leaf(root2, leaf2)        return leaf1 == leaf2